function setupNavBar(){const e=Array.prototype.slice.call(document.querySelectorAll(".navbar-burger"),0);e.length>0&&e.forEach(e=>{e.addEventListener("click",()=>{const t=e.dataset.target,n=document.getElementById(t);e.classList.toggle("is-active"),n.classList.toggle("is-active")})})}function getStarted(){const e=document.getElementById("back-btn"),t=document.getElementById("hero-title"),n=document.getElementById("get-started"),d=document.getElementById("wallet"),a=document.getElementById("get-wallet");d.addEventListener("click",()=>{t.innerText="Wallet",e.classList.remove("hidden"),n.classList.add("hidden"),a.classList.remove("hidden")}),e.addEventListener("click",()=>{t.innerText="Getting Started",e.classList.add("hidden"),n.classList.remove("hidden"),a.classList.add("hidden")})}function dataLinks(){document.querySelectorAll("[data-link]").forEach(e=>{e.addEventListener("click",()=>{document.location=e.dataset.link})})}document.addEventListener("DOMContentLoaded",()=>{setupNavBar(),getStarted(),dataLinks()});